#87 days since 09/10
all_days=[k for k in range(120)]
coins=[[0 for i in range(120)] for k in range(len(coins_days))]
money=[[0 for i in range(120)] for k in range(len(coins_days))]
avgs=[0 for k in range(len(coins_days))]
total_coins=[0 for i in range(120)]
total_money=[0 for i in range(120)]

for k in range(len(coins_days)):
    cd=coins_days[k]
    for i in range(len(cd)):
        date=cd[i][0]
        coin=cd[i][1]
        mount=int(date[5:7])
        
        day=int(date[8:10])
        if mount==1:
            mount=3
            day+=1
        i=mount%10
        print(mount,day,i*30+day,i)
        
        coins[k][i*30+day]=coin
        money[k][i*30+day]=coin*2.4
        total_coins[i*30+day]+=coin
        total_money[i*30+day]+=coin*2.4
    plt.subplot(2, 1, 1)
    plt.plot(all_days,coins[k])
    plt.xlabel("Days from begining")
    plt.ylabel("Coins")
    plt.subplot(2, 1, 2)
    plt.plot(all_days,money[k])
    plt.xlabel("Days from begining")
    plt.ylabel("Money ($)")

cummulate_total=[0 for i in range(120)]
